/* http://blog.bassta.bg/2013/05/smooth-page-scrolling-with-tweenmax/ */

var $scroll_container 				= $(window);
var $default_scroll_container		= $scroll_container;
var kill_tween_scroll				= false;
var is_IE 							= navigator.userAgent.toLowerCase().indexOf('msie') > -1;
var is_firefox 						= navigator.userAgent.toLowerCase().indexOf('firefox') > -1;
$(function(){
	
			//Window object
	
	var scrollTime 		= 0.4;			//Scroll time
	
	var scrollDistance 	= 170;		//Distance. Use smaller value for shorter scroll and greater value for longer scroll
	if (is_IE || is_firefox){ scrollDistance = 300; }
	
	
	$scroll_container.on("mousewheel DOMMouseScroll", function(event){
		if (kill_tween_scroll) return;
		event.preventDefault();	
		
										
		var delta = event.originalEvent.wheelDelta/120 || -event.originalEvent.detail/3;
		var scrollTop = $scroll_container.scrollTop();
		var finalScroll = scrollTop - parseInt(delta*scrollDistance);
		//console.log(finalScroll);
		TweenLite.to($scroll_container, scrollTime, {
			scrollTo : { y: finalScroll, autoKill:true },
				ease: Power1.easeOut,	//For more easing functions see http://api.greensock.com/js/com/greensock/easing/package-detail.html
				autoKill: true,
				overwrite: 5							
		});	
	});
	
});

